BEGIN TRANSACTION;
PRAGMA foreign_keys=OFF;
DROP TABLE IF EXISTS kb_item_category;
DROP TABLE IF EXISTS kb_favorite;
DROP TABLE IF EXISTS kb_download_log;
DROP TABLE IF EXISTS kb_attachment;
DROP TABLE IF EXISTS kb_item;
DROP TABLE IF EXISTS kb_category;
DROP TABLE IF EXISTS kb_column;
CREATE TABLE kb_attachment (
                  id INTEGER PRIMARY KEY AUTOINCREMENT,
                  item_id INTEGER NOT NULL,
                  file_name TEXT NOT NULL,
                  file_type TEXT DEFAULT NULL,
                  file_size INTEGER NOT NULL DEFAULT 0,
                  created_at TEXT NOT NULL DEFAULT (datetime('now')), file_url TEXT DEFAULT NULL,
                  FOREIGN KEY (item_id) REFERENCES kb_item(id) ON DELETE CASCADE
                );
CREATE TABLE kb_category (
                  id INTEGER PRIMARY KEY AUTOINCREMENT,
                  column_code TEXT NOT NULL,
                  name TEXT NOT NULL,
                  sort_no INTEGER NOT NULL DEFAULT 0,
                  created_at TEXT NOT NULL DEFAULT (datetime('now')),
                  UNIQUE (column_code, name)
                );
CREATE TABLE kb_column (
                  code TEXT PRIMARY KEY,
                  name TEXT NOT NULL,
                  description TEXT DEFAULT NULL,
                  sort_no INTEGER NOT NULL DEFAULT 0,
                  created_at TEXT NOT NULL DEFAULT (datetime('now'))
                );
CREATE TABLE kb_download_log (
                  id INTEGER PRIMARY KEY AUTOINCREMENT,
                  user_id TEXT NOT NULL,
                  item_id INTEGER NOT NULL,
                  created_at TEXT NOT NULL DEFAULT (datetime('now')),
                  FOREIGN KEY (item_id) REFERENCES kb_item(id) ON DELETE CASCADE
                );
CREATE TABLE kb_favorite (
                  user_id TEXT NOT NULL,
                  item_id INTEGER NOT NULL,
                  created_at TEXT NOT NULL DEFAULT (datetime('now')),
                  PRIMARY KEY (user_id, item_id),
                  FOREIGN KEY (item_id) REFERENCES kb_item(id) ON DELETE CASCADE
                );
CREATE TABLE kb_item (
                  id INTEGER PRIMARY KEY AUTOINCREMENT,
                  column_code TEXT NOT NULL,
                  title TEXT NOT NULL,
                  summary TEXT DEFAULT NULL,
                  content TEXT DEFAULT NULL,
                  publish_org TEXT DEFAULT NULL,
                  doc_no TEXT DEFAULT NULL,
                  publish_time TEXT DEFAULT NULL,
                  views INTEGER NOT NULL DEFAULT 0,
                  created_at TEXT NOT NULL DEFAULT (datetime('now')),
                  updated_at TEXT NOT NULL DEFAULT (datetime('now'))
                , author TEXT DEFAULT NULL, download_count INTEGER NOT NULL DEFAULT 0, favorite_count INTEGER NOT NULL DEFAULT 0, image_urls TEXT DEFAULT NULL, user_id TEXT DEFAULT NULL, view_count INTEGER NOT NULL DEFAULT 0, collect_count INTEGER NOT NULL DEFAULT 0, subtitle TEXT DEFAULT NULL, tags TEXT DEFAULT NULL, is_top INTEGER NOT NULL DEFAULT 0, seo_keywords TEXT DEFAULT NULL);
CREATE TABLE kb_item_category (
                  item_id INTEGER NOT NULL,
                  category_id INTEGER NOT NULL,
                  created_at TEXT NOT NULL DEFAULT (datetime('now')),
                  PRIMARY KEY (item_id, category_id),
                  FOREIGN KEY (item_id) REFERENCES kb_item(id) ON DELETE CASCADE,
                  FOREIGN KEY (category_id) REFERENCES kb_category(id) ON DELETE CASCADE
                );
DELETE FROM "sqlite_sequence";
COMMIT;
